import fs from 'fs';

const deLocale = JSON.parse(fs.readFileSync(new URL('./de/translation.json', import.meta.url)));
const enLocale = JSON.parse(fs.readFileSync(new URL('./en/translation.json', import.meta.url)));
const esLocale = JSON.parse(fs.readFileSync(new URL('./es/translation.json', import.meta.url)));
const itLocale = JSON.parse(fs.readFileSync(new URL('./it/translation.json', import.meta.url)));
const jaLocale = JSON.parse(fs.readFileSync(new URL('./ja/translation.json', import.meta.url)));
const plLocale = JSON.parse(fs.readFileSync(new URL('./pl/translation.json', import.meta.url)));
const ptBrLocale = JSON.parse(fs.readFileSync(new URL('./pt-BR/translation.json', import.meta.url)));
const uaLocale = JSON.parse(fs.readFileSync(new URL('./ua/translation.json', import.meta.url)));
const zhCNLocale = JSON.parse(fs.readFileSync(new URL('./zh-CN/translation.json', import.meta.url)));

export * from './config.js';
export * from './default-config.js';

export const locales = {
  de: deLocale,
  en: enLocale,
  es: esLocale,
  it: itLocale,
  ja: jaLocale,
  pl: plLocale,
  'pt-BR': ptBrLocale,
  ua: uaLocale,
  'zh-CN': zhCNLocale
};